const json = $input.item.json;
const message_text = json.message ? json.message.text : json.text;
const chat_id = json.chat_id || (json.message && json.message.chat ? json.message.chat.id : null);

// User Mapping
const chatMap = {
    "5785514136": "Alberto",
    "-5223730192": "Alberto y Laura",
    "-5234536185": "Alberto y Laura",
    "PLACEHOLDER": "Laura"
};
const who = chatMap[String(chat_id)] || "Desconocido";

const prompt_id = 'meals.tracker';
const options = {};

return {
  json: {
    prompt_id,
    options,
    chat_id,
    who,
    input: {
        input_type: 'TEXT',
        input_text: message_text
    },
    original_msg: message_text
  }
};

const fs = require('fs');
const item = $input.item.json;

// LLM Output
// Adapted for generic LLM response structure. Assuming the adapter returns parseable JSON in 'output' or properties directly.
const data = item.output || item;

const food = data.food || 'Desconocido';
const type = data.type || 'other';
const date_str = data.date || new Date().toISOString().split('T')[0];
const type_capitalized = type.charAt(0).toUpperCase() + type.slice(1);

// Metadata
const prepareNode = $('Prepare LLM Input').first().json;
const who = prepareNode.who;
const source = 'telegram';
const timestamp = new Date().toISOString();

// CSV Line
// timestamp,date_str,who,type,food,source
const line = `${timestamp},${date_str},${who},${type},"${food}",${source}\n`;

const csvPath = '/data/meals/meals.csv';

try {
    if (!fs.existsSync(csvPath)) {
        // Header
        fs.writeFileSync(csvPath, 'timestamp,date_str,who,type,food,source\n');
    }
    fs.appendFileSync(csvPath, line);
} catch (e) {
    throw new Error(`Failed to write to CSV: ${e.message}`);
}

// Read Catalog for next step
let catalog = [];
try {
    const catalogPath = '/data/inventory/food_stock_catalog.json';
    if (fs.existsSync(catalogPath)) {
        catalog = JSON.parse(fs.readFileSync(catalogPath, 'utf8'));
        if (catalog.products) catalog = catalog.products;
    }
} catch (e) {
    // Ignore if fails, logic will proceed with empty catalog
}

return {
  json: {
    ...item,
    logged_food: food,
    logged_type: type,
    logged_date: date_str,
    logged_who: who,
    catalog: catalog
  }
};

// Override Adapter Logic for Custom Replacements
// This node is a hack to replace {{catalog_json}} before the adapter runs, if I was calling the adapter components directly.
// But I am calling the adapter WORKFLOW.
// The adapter workflow does NOT support arbitrary variable replacement.
// SO, I must overwrite the prompt inputs in the node 'Prepare Ingredients' to just be compatible with a generic prompt:
// "Identify ingredients... Input: 
// Catalog: ... 
// Meal: ..."
// I will REWRITE the prompt file in a subsequent step to be standard context-based.
// And here I return the full context string.

const catalog = $input.first().json.catalog || [];
const miniCatalog = catalog.map(p => ({
    id: p.id,
    name: p.display_name || p.name,
    synonyms: p.synonyms
}));
const meal = $input.first().json.logged_food;

const fullInput = `Catalog:\n${JSON.stringify(miniCatalog, null, 2)}\n\nMeal:\n"${meal}"`;

return {
  json: {
    prompt_id: 'meals.ingredient_mapper',
    input: {
        input_type: 'TEXT',
        input_text: fullInput
    }
  }
};

const fs = require('fs');
const item = $input.item.json;
const ingredientsData = item.output || item;
const ingredients = ingredientsData.ingredients || [];

// Get previous context
const logNode = $('Log and Load Catalog').first().json;
const mealName = logNode.logged_food;
const who = logNode.logged_who;
const type = logNode.logged_type;

// Valid flags
const isLunch = type.toLowerCase() === 'comida';
const isDinner = type.toLowerCase() === 'cena';
const isAlberto = who.includes('Alberto');
const isLaura = who.includes('Laura');

// Normalize Meal Name (Capitalize)
const normalizedName = mealName.trim().charAt(0).toUpperCase() + mealName.trim().slice(1);

// Read Meals Catalog
const mealsCatalogPath = '/data/inventory/meals_catalog.json';
let mealsCatalog = [];
try {
    if (fs.existsSync(mealsCatalogPath)) {
        mealsCatalog = JSON.parse(fs.readFileSync(mealsCatalogPath, 'utf8'));
    }
} catch (e) {
    mealsCatalog = [];
}

// Find existing meal
let mealEntry = mealsCatalog.find(m => m.name.toLowerCase() === normalizedName.toLowerCase());
let isNew = false;

if (!mealEntry) {
    isNew = true;
    mealEntry = {
        name: normalizedName,
        valid_for_lunch: false,
        valid_for_dinner: false,
        valid_for_alberto: false,
        valid_for_laura: false,
        ingredients: []
    };
    mealsCatalog.push(mealEntry);
}

// Update Flags (OR logic)
if (isLunch) mealEntry.valid_for_lunch = true;
if (isDinner) mealEntry.valid_for_dinner = true;
if (isAlberto) mealEntry.valid_for_alberto = true;
if (isLaura) mealEntry.valid_for_laura = true;

// Update Ingredients (Merge unique)
// If it's a new meal, just take them.
// If existing, we append new ones found? Or replace? 
// User said: "Para rellenar el array... se podrÃ¡ preguntar al usuario".
// Implies we define the ingredients. If we extract more ingredients now, we should probably add them if not present.
const newIngredients = ingredients.filter(i => !mealEntry.ingredients.includes(i));
mealEntry.ingredients = [...mealEntry.ingredients, ...newIngredients];

// Save Catalog
try {
    fs.writeFileSync(mealsCatalogPath, JSON.stringify(mealsCatalog, null, 2));
} catch (e) {
    throw new Error('Failed to save meals catalog: ' + e.message);
}

return {
  json: {
    ...item,
    meal_name: normalizedName,
    new_ingredients: newIngredients,
    is_new: isNew,
    updated_entry: mealEntry
  }
};

